import React, { useState } from "react";
import { Container, Box, CircularProgress, CssBaseline } from "@mui/material";
import FileUploader from "./components/FileUploader";
import LanguageSelector from "./components/LanguageSelector";
import ApiKeyInput from "./components/ApiKeyInput";
import ApiKeyValidator from "./components/ApiKeyValidator";
import DownloadButton from "./components/DownloadButton";
import { translateSrt } from "./utils/translateSrt";

const App: React.FC = () => {
  const [file, setFile] = useState<File | null>(null);
  const [language, setLanguage] = useState("");
  const [apiKey, setApiKey] = useState("");
  const [loading, setLoading] = useState(false);

  const handleTranslateAndDownload = async () => {
    if (!file || !language || !apiKey) return;

    setLoading(true);
    const reader = new FileReader();
    reader.onload = async (event) => {
      const content = event.target?.result as string;
      try {
        const translated = await translateSrt(content, language, apiKey);
        const blob = new Blob([translated], { type: "text/plain" });
        const link = document.createElement("a");
        link.href = URL.createObjectURL(blob);
        link.download = `translated_${file.name}`;
        link.click();
      } catch (error) {
        console.error("Translation failed:", error);
      }
      setLoading(false);
    };
    reader.readAsText(file);
  };

  return (
    <Container>
      <CssBaseline />
      <Box my={4}>
        <FileUploader onFileSelect={setFile} />
        <ApiKeyInput apiKey={apiKey} onApiKeyChange={setApiKey} />
        <ApiKeyValidator apiKey={apiKey} />
        <LanguageSelector selectedLanguage={language} onLanguageChange={setLanguage} />
        {loading ? <CircularProgress /> : (
          <DownloadButton onClick={handleTranslateAndDownload} disabled={!file || !language || !apiKey} />
        )}
      </Box>
    </Container>
  );
};

export default App;
