import React, { useState } from 'react';
import { 
  Container, 
  Paper, 
  Snackbar, 
  Alert, 
  Button, 
  Typography, 
  Box,
  Divider,
  LinearProgress,
  Stack
} from '@mui/material';
import FileUploader from './components/FileUploader';
import LanguageSelector from './components/LanguageSelector';
import { Language } from './types';
import { readFileAsText } from './utils/fileHelper';

interface TranslationResponse {
  translated_text: string;
  status: string;
}

// Define FileUploaderProps interface
interface FileUploaderProps {
  onFileSelect: (file: File) => void;
  maxSize: number;
  allowedTypes: string[];
}

const App: React.FC = () => {
  // Environment variables with type safety
  const API_URL = process.env.REACT_APP_API_URL || 'http://127.0.0.1:8000/translate';
  const MAX_FILE_SIZE = Number(process.env.REACT_APP_MAX_FILE_SIZE) || 5242880;
  const ALLOWED_FILE_TYPES = (process.env.REACT_APP_ALLOWED_FILE_TYPES?.split(',') || ['.srt', '.txt'])
    .map(type => type.trim());

  // State management
  const [file, setFile] = useState<File | null>(null);
  const [language, setLanguage] = useState<Language>('es');
  const [progress, setProgress] = useState<number>(0);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<boolean>(false);

  const handleFileSelect = (selectedFile: File) => {
    if (selectedFile.size > MAX_FILE_SIZE) {
      setError(`File size exceeds maximum limit of ${MAX_FILE_SIZE / 1024 / 1024}MB`);
      return;
    }

    const fileExtension = `.${selectedFile.name.split('.').pop()?.toLowerCase()}`;
    if (!ALLOWED_FILE_TYPES.includes(fileExtension)) {
      setError(`Invalid file type. Allowed types: ${ALLOWED_FILE_TYPES.join(', ')}`);
      return;
    }

    setFile(selectedFile);
    setError(null);
    console.log('File selected:', selectedFile.name);
  };

  const translateText = async (content: string, targetLanguage: string): Promise<string> => {
    try {
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          text: content,
          target_language: targetLanguage
        })
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data: TranslationResponse = await response.json();
      return data.translated_text;
    } catch (error) {
      throw new Error(error instanceof Error ? error.message : 'Translation API request failed');
    }
  };

  const downloadFile = (content: string, originalFileName: string) => {
    const blob = new Blob([content], { type: 'text/plain;charset=utf-8' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `translated_${originalFileName}`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
  };

  const handleTranslate = async () => {
    if (!file) {
      setError('Please select a file to translate');
      return;
    }

    setLoading(true);
    setProgress(0);
    setError(null);

    try {
      const fileContent = await readFileAsText(file);
      setProgress(30);

      const translatedContent = await translateText(fileContent, language);
      setProgress(70);

      downloadFile(translatedContent, file.name);
      setProgress(100);
      setSuccess(true);

      setTimeout(() => {
        setProgress(0);
        setSuccess(false);
      }, 3000);

    } catch (err) {
      setError(err instanceof Error ? err.message : 'Translation failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ borderRadius: 2 }}>
        <Box sx={{ p: 3, bgcolor: 'primary.main', color: 'white', borderTopLeftRadius: 8, borderTopRightRadius: 8 }}>
          <Typography variant="h5" component="h1">
            SRT File Translator
          </Typography>
          <Typography variant="body2" sx={{ mt: 1, opacity: 0.8 }}>
            Translate your subtitle files to any language
          </Typography>
        </Box>

        <Stack spacing={3} sx={{ p: 3 }}>
          <Box>
            <Typography variant="h6" gutterBottom>
              1. Upload SRT File
            </Typography>
            <FileUploader 
              onFileSelect={handleFileSelect}
              maxSize={MAX_FILE_SIZE}
              allowedTypes={ALLOWED_FILE_TYPES}
            />
            {file && (
              <Typography variant="body2" sx={{ mt: 1, color: 'success.main' }}>
                âœ“ Selected file: {file.name}
              </Typography>
            )}
          </Box>

          <Divider />

          <Box>
            <Typography variant="h6" gutterBottom>
              2. Select Target Language
            </Typography>
            <LanguageSelector language={language} setLanguage={setLanguage} />
          </Box>

          <Button
            size="large"
            fullWidth
            variant="contained"
            onClick={handleTranslate}
            disabled={loading || !file}
            sx={{ 
              mt: 2,
              py: 1.5,
              fontSize: '1.1rem',
              fontWeight: 'bold'
            }}
          >
            {loading ? 'Translating...' : 'Start Translation'}
          </Button>

          {loading && (
            <Box sx={{ width: '100%' }}>
              <LinearProgress 
                variant="determinate" 
                value={progress}
                sx={{ 
                  height: 8,
                  borderRadius: 4,
                  bgcolor: 'grey.200'
                }}
              />
              <Typography variant="body2" color="text.secondary" align="center" sx={{ mt: 1 }}>
                Translation Progress: {progress}%
              </Typography>
            </Box>
          )}
        </Stack>
      </Paper>

      <Snackbar 
        open={!!error} 
        autoHideDuration={6000} 
        onClose={() => setError(null)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert 
          severity="error" 
          onClose={() => setError(null)}
          variant="filled"
          sx={{ width: '100%' }}
        >
          {error}
        </Alert>
      </Snackbar>

      <Snackbar 
        open={success} 
        autoHideDuration={3000} 
        onClose={() => setSuccess(false)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert 
          severity="success" 
          variant="filled"
          sx={{ width: '100%' }}
        >
          Translation completed successfully!
        </Alert>
      </Snackbar>
    </Container>
  );
};

export default App;
